package week1.day1;

public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		 //Always use f/F at the end of value for float, l/L for long, "" for String, '' for char
		 float browserVersion = 100.2f;
		 String browserName = "firefox";
		 boolean isVisible = true;
		 int releaseYear = 1998;
		 char browserLogo = 'f';
		 
		 System.out.println("Browser Version is: "+browserVersion+"\n" + "Browser Name is: " +browserName+"\n"+ "IsVisible value is: "+isVisible);
		 System.out.println("Release Year is: "+releaseYear+"\n"+ "Browser Logo is: "+browserLogo);
	}

}
